function numberFormatting(id,roundNum){
    var STR = jQuery("#"+id).val().split(".");
    if(STR.length > 1 && STR[1] != 'undefined'){
 jQuery("#"+id).val(Globalize.format(Math.abs(STR[0].replace(/[^\d\.]/g,"")),"n","en-IN"))
        var roundNum = parseInt(roundNum)
        if(isNaN(STR[1])){
            alert("Invalid Entry");
            $("#"+id).focus();
            return false;
        }else{
 $("#"+id).attr("value",$("#"+id).val()+"."+STR[1].substr(0,roundNum));
        }
    }else{
    	var str= "";
    	for(var i=0;i<roundNum; i++){
    		str = str+"0";
    	}
    	jQuery("#"+id).val(Globalize.format(Math.abs(jQuery("#"+id).val().replace(/[^\d\.]/g,"")),"n","en-IN")+"."+str)
    }
}
